import fastify from "fastify";
import config from "./config";
import { loggerConfig } from "./logger";
import userRoutes from "../modules/user/user.route";
import authRoutes from "../modules/auth/auth.route";
import miscRoutes from "../modules/misc/misc.route";
import fjwt from "./plugins/jwt";
import {
	jsonSchemaTransform,
	serializerCompiler,
	validatorCompiler,
} from "fastify-type-provider-zod";
import fastifySwagger from "@fastify/swagger";
import fastifySwaggerUi from "@fastify/swagger-ui";
import { version } from "../../package.json";
import errorHandler from "./plugins/errorHandler";

// We separate the server creation into its own function to make testing easier
const createServer = async () => {
	// TODO: Register CORS plugin for admin panel
	const app = fastify({
		logger: loggerConfig[config.NODE_ENV] ?? true,
	});

	//   Register Plugins

	app.register(errorHandler);

	app.register(fjwt);

	// Zod and swagger
	app.setValidatorCompiler(validatorCompiler);
	app.setSerializerCompiler(serializerCompiler);

	app.register(fastifySwagger, {
		transform: jsonSchemaTransform,
		openapi: {
			info: {
				title: "Yakka API",
				description: "Yakka API",
				version,
			},
			components: {
				securitySchemes: {
					bearerAuth: {
						type: "http",
						scheme: "bearer",
						bearerFormat: "JWT",
						description:
							"JWT Authorization header using the Bearer scheme. Put your access token in the header. Example: 'Authorization: Bearer {token}'",
					},
				},
			},
		},
	});

	app.register(fastifySwaggerUi, {
		routePrefix: "/docs",
		staticCSP: true,
	});

	// Register routes

	// Finally, register the routes
	app.register(miscRoutes);
	app.register(userRoutes, { prefix: "/api/users" });
	app.register(authRoutes, { prefix: "/api/auth" });
	// app.ready().then(() => {
	// 	// if (config.NODE_ENV === 'production') {
	// 	//   server.scheduler.addSimpleIntervalJob(commentCounterJob);
	// 	//   server.scheduler.addSimpleIntervalJob(likeCounterJob);
	// 	// }

	// });
	return app;
};

export default createServer;

import { z } from "zod";
import * as dotenv from "dotenv";
dotenv.config();
const envSchema = z.object({
	HOST: z.string().default("0.0.0.0"),
	PORT: z.number().default(4000),
	NODE_ENV: z
		.enum(["development", "production", "test"])
		.default("development"),
	BASE_URL: z.string().url(),
	APP_URL_SCHEME: z.string(),
	DATABASE_URL: z.string(),
	SHADOW_DATABASE_URL: z.string(),
	RESET_PASSWORD_TOKEN_SECRET: z.string(),
	ENCRYPTION_SECRET: z.string(),
	GOOGLE_IOS_CLIENT_ID: z.string(),
	GOOGLE_ANDROID_CLIENT_ID: z.string(),
	APPLE_CLIENT_ID: z.string(),
	APPLE_TEAM_ID: z.string(),
	APPLE_KEY_ID: z.string(),
	APPLE_CLIENT_SECRET: z.string(),
	FACEBOOK_APP_SECRET: z.string(),
	LINKEDIN_CLIENT_ID: z.string(),
	LINKEDIN_CLIENT_SECRET: z.string(),
	SNS_LAMBDA_URL: z.string(),
	MAILJET_API_KEY: z.string(),
	MAILJET_API_SECRET: z.string(),
	S3_BUCKET_NAME: z.string(),
	S3_REGION: z.string(),
	S3_ACCESS_KEY: z.string(),
	S3_SECRET_KEY: z.string(),
});

const parsed = envSchema.safeParse(process.env);

if (!parsed.success) {
	console.error(
		"âŒ Invalid environment variables:",
		JSON.stringify(parsed.error.format(), null, 4)
	);
	process.exit(1);
}

export default parsed.data;

import pino from "pino";
import config from "./config";
// https://www.fastify.io/docs/latest/Reference/Logging/
export const loggerConfig = {
	// Provide pretty logs in development
	// Format the time and ignore hostname and process id
	development: {
		transport: {
			target: "pino-pretty",
			options: {
				translateTime: "HH:MM:ss Z",
				ignore: "pid,hostname",
			},
		},
	},
	// Provide normal logs in production
	production: true,
	// Don't log in test
	test: false,
};

// Fastify will use a built in logger and automatically log events
// The logger is attatched the the fastify instance and request objects
// So we can use it in our routes
// However, we also export a global logger here to use it in other places
// where we don't have access to the fastify instance
const selectedConfig = loggerConfig[config.NODE_ENV];
const logger = pino(
	typeof selectedConfig === "boolean"
		? {
				enabled: selectedConfig,
		  }
		: selectedConfig
);

export default logger;

import { FastifyError, FastifyInstance, FastifyPluginOptions } from "fastify";
import {
	defaultResponseSchema,
	errorResponseSchema,
} from "../../types/globalSchemas";
import {
	getVerificationImageHandler,
	uploadVerificationImageHandler,
} from "./user.controller";
import {
	getVerificationImageSchema,
	uploadVerificationImageSchema,
} from "./user.schema";
const userRoutes = (
	app: FastifyInstance,
	_: FastifyPluginOptions,
	done: (err?: FastifyError) => void
) => {
	app.get(
		"/image-verification",
		{
			schema: {
				summary: "Get a random gesture example image.",
				response: {
					200: getVerificationImageSchema,
				},
				security: [
					{
						bearerAuth: [],
					},
				],
			},
			onRequest: app.authenticate,
		},
		getVerificationImageHandler
	);

	// upload image url
	// TODO: Look into making a type or somethhing which automatically adds the security schema and the authenticate hook
	app.post(
		"/image-verification",
		{
			schema: {
				summary: "Upload a verification image",
				body: uploadVerificationImageSchema,
				response: {
					// 200: defaultResponseSchema,
					404: errorResponseSchema.describe("Gesture not found"),
					409: errorResponseSchema.describe(
						"User has already uploaded an image"
					),
					500: errorResponseSchema.describe("Could not upload image"),
				},
				security: [
					{
						bearerAuth: [],
					},
				],
			},
			onRequest: app.authenticate,
		},
		uploadVerificationImageHandler
	);

	done();
};

export default userRoutes;

import { z } from "zod";

export const defaultResponseSchema = z.object({
	message: z.string(),
});

export const errorResponseSchema = defaultResponseSchema.extend({
	errorCode: z.string().optional(),
});

import { PrismaClientKnownRequestError } from "@prisma/client/runtime";
import type { FastifyRequest, FastifyReply } from "fastify";
import { formatS3Url, uploadBase64ImageToS3 } from "../../utils/aws";
import prisma from "../../utils/prisma";
import { UploadVerificationImageSchema } from "./user.schema";

export const getVerificationImageHandler = async (
	request: FastifyRequest,
	reply: FastifyReply
) => {
	const gestureCount = await prisma.gesture.count();
	const randomGesture = await prisma.gesture.findFirst({
		skip: Math.floor(Math.random() * gestureCount),
	});

	if (!randomGesture) {
		return reply.status(500).send({ error: "Could not find a gesture." });
	}

	const { imageName, ...rest } = randomGesture;

	return reply.send({
		...rest,
		imageUrl: formatS3Url({
			fileName: randomGesture.imageName,
			path: "gestures/examples",
		}),
	});
};

export const uploadVerificationImageHandler = async (
	request: FastifyRequest<{
		Body: UploadVerificationImageSchema;
	}>,
	reply: FastifyReply
) => {
	try {
		const { base64, gestureId } = request.body;

		const gesture = await prisma.gesture.findUnique({
			where: {
				id: gestureId,
			},
		});

		if (!gesture) {
			return reply.status(404).send({ error: "Gesture not found" });
		}

		const { url, imageName } = await uploadBase64ImageToS3({
			base64,
			path: "gestures/users",
		});
		await prisma.userVerificationImage.create({
			data: {
				userId: request.user.id,
				gestureId,
				imageName,
			},
		});
		return reply.send({ url, imageName });
	} catch (error) {
		console.error(error);
		if (error instanceof PrismaClientKnownRequestError) {
			if (error.code === "P2002") {
				return reply
					.status(409)
					.send({ message: "You have already uploaded an image." });
			}
		}
		return reply.status(500).send({ message: "Could not upload image" });
	}
};

import AWS from "aws-sdk";
import { PutObjectRequest } from "aws-sdk/clients/s3";
import { nanoid } from "nanoid";
import config from "./config";
// TODO: Ideally we would block all public access
// And configure a bucket polciy to allow read access to some objects
// https://aws.amazon.com/premiumsupport/knowledge-center/read-access-objects-s3-bucket/
export const s3 = new AWS.S3({
	region: config.S3_REGION,
	accessKeyId: config.S3_ACCESS_KEY,
	secretAccessKey: config.S3_SECRET_KEY,
});

// Helper functions

export const formatS3Url = ({
	path,
	fileName,
}: {
	path: string;
	fileName: string;
}) =>
	`https://${config.S3_BUCKET_NAME}.s3.${config.S3_REGION}.amazonaws.com/${path}/${fileName}`;

interface UploadToS3Params {
	base64: string;
	path: string;
}

export const uploadBase64ImageToS3 = async (params: UploadToS3Params) => {
	const base64Data = Buffer.from(
		params.base64.replace(/^data:image\/\w+;base64,/, ""),
		"base64"
	);
	const type = params.base64.split(";")[0].split("/")[1];
	const fileName = `${nanoid()}.${type}`;
	const config: PutObjectRequest = {
		Bucket: process.env.S3_BUCKET_NAME!,
		Key: `${params.path}/${fileName}`,
		Body: base64Data,
		ContentEncoding: "base64",
		ContentType: `image/${type}`,
	};

	// Error should be caught by caller
	const upload = await s3.putObject(config).promise();

	//  Return the URL of the uploaded image

	return {
		url: formatS3Url({
			fileName,
			path: params.path,
		}),
		imageName: fileName,
	};
};
